export default class ResourceListWithProducts extends React.Component {
  state = {
    selectedItems: [],
    products:[],
    queryValue:'',
    sortValue:'',
    typeselected:''
  };
  componentDidMount() {
    fetch("https://aladdin-dropshipper-server.herokuapp.com/products", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
