import React from 'react';
import {Page,Avatar,Filters, Card,Button, ResourceItem, ResourceList, TextStyle, TextField, Layout, Select, Thumbnail} from '@shopify/polaris';
import {CirclePlusMinor} from '@shopify/polaris-icons';
const fetch = require('node-fetch');
const stripHtml =require('string-strip-html');
var product={title:""};

export default class ResourceListWithProducts extends React.Component {
  state = {
    selectedItems: [],
    products:[],
    queryValue:'',
    sortValue:'',
    typeselected:''
  };
  componentDidMount() {
    fetch("https://aladdin-dropshipper-server.herokuapp.com/products", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(product)
    }).then(res => res.json()) // expecting a json response
    .then(items => {console.log(items)
    this.setState({products:items});
  }
    );
 }
  renderItem(item) {
    const body_html=stripHtml( item.body_html);
    const {id, url, title,avatarSource} = item;
    const media = <Thumbnail product size="large" name={name} source={avatarSource} />;

    return (

      <ResourceItem

        id={id}

        media={media}
        accessibilityLabel={`View details for ${title}`}
      >
        <h3>
          <TextStyle variation="strong">{title}</TextStyle>
        </h3>
        <div><p>{body_html}</p></div>
        <Button  onClick={(e) =>{
          e.preventDefault();
          alert(id)}}
 icon={CirclePlusMinor}>Add a product</Button>
      </ResourceItem>

    );
  }
  onSelectionChange(items){
    const idsFromResources = items.map((item) => item);       
    this.setState({ selectedItems: idsFromResources });
  }
  onSortChange(selected){
      this.setState({ SortValue: selected });
      console.log(`Sort option changed to ${selected}.`);  
  }
  handleQueryValueChange(value){
    this.setState({queryValue:value})
    console.log('handleQueryValueChange'+value)
  }
  handleQueryValueRemove(value){
    this.setState({queryValue:null})
    console.log('handleQueryValueRemove'+value)
  }
  render() {
    const {
      selectedItems,
      queryValue,
      sortValue,
      typeselected,
      products
    }=this.state;
    const options = [
      {label: 'Today', value: 'today'},
      {label: 'Yesterday', value: 'yesterday'},
      {label: 'Last 7 days', value: 'lastWeek'},
    ];
    const filters = [
      {
        key: 'taggedWith',
        label: 'Tagged with',
        filter: (
          <Select
          label="Date range"
          options={options}
          onChange={()=>handleSelectChange()}
          value={typeselected}
        />
        ),
        shortcut: true,
      }
    ];

    const filterControl = (
      <Filters
        queryValue={queryValue}
        filters={filters}   
        onQueryChange={(value)=>this.handleQueryValueChange(value)}
        onQueryClear={(value)=>this.handleQueryValueRemove(value)}
      >
        <div style={{paddingLeft: '8px'}}>
          <Button onClick={() => console.log('New filter saved')}>Save</Button>
        </div>
      </Filters>
    );
  return (
    <Page>
    <Card>

      <ResourceList
        resourceName={{
          singular: 'product',
          plural: 'products',
        }}
        items={products}
        renderItem={(item)=>this.renderItem(item)}
        selectedItems={selectedItems}
        onSelectionChange={(items)=>this.onSelectionChange(items)}
        promotedBulkActions={ [
          {
            content: 'Import Products',
            onAction: () => console.log('Todo: implement bulk edit'),
          },
        ]}

        sortOptions={[
          {label: 'Newest update', value: 'DATE_MODIFIED_DESC'},
          {label: 'Oldest update', value: 'DATE_MODIFIED_ASC'},
        ]}
        onSortChange={(selected) =>this.onSortChange(selected)}
        filterControl={filterControl}

      />

    </Card>
    </Page>
  );


}

}
