import React from 'react';
import {Page,FormLayout,Form, Link,Button, InlineError ,Select, Card, TextField, Layout,Thumbnail} from '@shopify/polaris';
import {CirclePlusMinor} from '@shopify/polaris-icons';
import {Elements} from '@stripe/react-stripe-js';
import {loadStripe} from '@stripe/stripe-js';
import CardForm from '../components/CardForm';
import store from 'store-js';
const stripePromise = loadStripe("pk_test_TYooMQauvdEDq54NiTphI7jx");

export default class Singin extends React.Component {
    state = {
        email:'',
        password:'',
        roleselected:'',
        isInvalid:false,
        isInvalidPassword:false,
        role:'dropshipper',
        errorMessage :'',
        errorMessagePassword:'',
        errorMessageRole:''
      };

      handleSubmit(_event){
        const url = 'https://aladdin-dropshipper-server.herokuapp.com'
          console.log(_event)  

          data:{}
          fetch(url, {
            method: "POST",
            headers: {
              "Content-Type": "application/json"
            },
            body: JSON.stringify({product})
          }).then(res => res.json())
          .then((json) =>{ console.log(json)
          store.set('token', json.token);
          store.set('userRole', this.state.roleselected);
          this.setState({email:'',  password:'' })
           } );


      }

      handleChoiceChange(value){
        console.log(value)
        this.setState({dropshipper:value})
      }
      handlePasswordChange(value){
        console.log(value)
        this.setState({password:value})
      }
      handleEmailChange(value){
        if(value.length < 3){
          this.setState({isInvalid:true})
        }
        if(value.length > 3){
          this.setState({isInvalid:false})
        }
       let errorMessage = this.state.isInvalid
    ? 'Enter 3 or more characters for product type is equal to'
    : '';

        console.log(value)
        this.setState({email:value,errorMessage:errorMessage})
    }
    handleRoleChange(value){
      console.log(value)
      this.setState({roleselected:value})
    }

    render() {
        const{email,password,roleselected,errorMessage,errorMessagePassword,errorMessageRole,isInvalid,isInvalidPassword}=this.state;

    return (
        <Page title="SignIn" separator>  
          <Layout>

        <Layout.Section
          title=""
          description=""
        >
        <Card sectioned>  
 <Form onSubmit={(_event)=>this.handleSubmit(_event)}>
      <FormLayout>


        <TextField
          value={email}
          onChange={(value) => this.handleEmailChange(value)}
          label="Email"
          type="email"
          error={isInvalid}
          id="email"
          helpText={
            <span>
              Enter your email Address.
            </span>
          }
        />
          <div style={{marginTop: '4px'}}>
          <InlineError message={errorMessage} fieldID={email} />
        </div>
             <TextField
          value={password}
          onChange={(value) => this.handlePasswordChange(value)}
          label="Password"
          type="password"
          error={isInvalidPassword}
          id="password"
          helpText={
            <span>
              Enter your email Password.
            </span>
          }
        />
         <div style={{marginTop: '4px'}}>
          <InlineError message={errorMessagePassword} fieldID={'password'} />
        </div>
        <Select
        label="Select Role"
        options={[
          {label: 'Seller', value: 'seller'},
          {label: 'DropShipper', value: 'dropshipper'},
        ]}
        id="role"
        selected={[roleselected]}
        onChange={(value)=>this.handleRoleChange(value)}
      />
          <div style={{marginTop: '4px'}}>
          <InlineError message={errorMessageRole} fieldID={'role'} />
        </div>

        <Button submit>Sign In</Button>
        <Link url="/signup">Not Resgistered?</Link>
           </FormLayout>
    </Form>
    </Card> 
    </Layout.Section>
    </Layout>
        </Page>

    );

    }

}
