import React from 'react';
import {Page,FormLayout,Form, Link,Button, InlineError ,Select, TextStyle, TextField, Layout,Thumbnail} from '@shopify/polaris';
import {CirclePlusMinor} from '@shopify/polaris-icons';
import {loadStripe} from '@stripe/stripe-js';
import CardForm from '../components/CardForm';

export default class Singup extends React.Component {
    state = {
        name:'',
        email:'',
        password:'',
        roleselected:'',
        isInvalid:false,
        isInvalidPassword:false,
        role:'dropshipper',
        errorMessage :'',
        errorMessagePassword:'',
        errorMessageRole:''
      };
   
      handleSubmit(_event){
        const{email,password,roleselected}=this.state;
        var user={email:email,password:password,roleselected:roleselected};
     console.log(product)
     fetch("https://aladdin-dropshipper-server.herokuapp.com/signup", {
       method: "POST",
       headers: {
         "Content-Type": "application/json",
       },
       body: JSON.stringify(user)
     }).then(res=>res.json()).then((json) =>{ console.log(json)})
          console.log(_event)  
          this.setState({email:'',  password:'' })
      }
      
      handleChoiceChange(value){
        console.log(value)
        this.setState({dropshipper:value})
      }
      handlePasswordChange(value){
        console.log(value)
        this.setState({password:value})
      }
      handleEmailChange(value){
        if(value.length < 3){
          this.setState({isInvalid:true})
        }
        if(value.length > 3){
          this.setState({isInvalid:false})
        }
       let errorMessage = this.state.isInvalid
    ? 'Enter 3 or more characters for product type is equal to'
    : '';
 
        console.log(value)
        this.setState({email:value,errorMessage:errorMessage})
    }
    handleRoleChange(value){
      console.log(value)
      this.setState({roleselected:value})
    }
       
    render() {
        const{name,email,password,roleselected,errorMessageName,errorMessage,errorMessagePassword,errorMessageRole,isInvalid,isInvalidPassword}=this.state;
       
    return (
        <Page title="Signup" separator>     
 <Form onSubmit={(_event)=>this.handleSubmit(_event)}>
      <FormLayout>
      <TextField
          value={name}
          onChange={(value) => this.handlefirstNameChange(value)}
          label="FirstName"
          type="text"
          error={isInvalid}
          id="name"
          helpText={
            <span>
              Enter your Full Name.
            </span>
          }
        />
          <div style={{marginTop: '4px'}}>
          <InlineError message={errorMessageName} fieldID={'name'} />
        </div>
     
        <TextField
          value={email}
          onChange={(value) => this.handleEmailChange(value)}
          label="Email"
          type="email"
          error={isInvalid}
          id="email"
          helpText={
            <span>
              Enter your email Address.
            </span>
          }
        />
          <div style={{marginTop: '4px'}}>
          <InlineError message={errorMessage} fieldID={'email'} />
        </div>
             <TextField
          value={password}
          onChange={(value) => this.handlePasswordChange(value)}
          label="Password"
          type="password"
          error={isInvalidPassword}
          id="password"
          helpText={
            <span>
              Enter your email Password.
            </span>
          }
        />
         <div style={{marginTop: '4px'}}>
          <InlineError message={errorMessagePassword} fieldID={'password'} />
        </div>
        <Select
        label="Select Role"
        options={[
          {label: 'Seller', value: 'seller'},
          {label: 'DropShipper', value: 'dropShipper'},
        ]}
        id="role"
        selected={[roleselected]}
        onChange={(value)=>this.handleRoleChange(value)}
      />
          <div style={{marginTop: '4px'}}>
          <InlineError message={errorMessageRole} fieldID={'role'} />
        </div>

        <Button submit>Sign In</Button>
        <Link url="/index">Already Resgistered?</Link>
           </FormLayout>
    </Form>
        </Page>

    );

    }
    
}
