import React from 'react';
import {Page,FormLayout,Form, Card,Button, Checkbox, ResourceList, TextStyle, TextField, Layout, Select, Thumbnail} from '@shopify/polaris';
import {CirclePlusMinor} from '@shopify/polaris-icons';
import {Elements} from '@stripe/react-stripe-js';
import {loadStripe} from '@stripe/stripe-js';
import CardForm from '../components/CardForm';
const stripePromise = loadStripe("pk_test_TYooMQauvdEDq54NiTphI7jx");

export default class Singin extends React.Component {
    state = {
        email:'',
        newsletter:''
      };

      handleSubmit(_event){
          console.log(_event)

          this.setState({email:'',  newsletter:false })

      }

      handleNewsLetterChange(value){
        console.log(value)
        this.setState({newsletter:value})
      }
      handleEmailChange(value){
        console.log(value)
        this.setState({email:value})
    }

    render() {
        const{newsletter,email}=this.state;
    return (
        <Page title="SignIn" separator>     
 <Form onSubmit={(_event)=>this.handleSubmit(_event)}>
      <FormLayout>


        <TextField
          value={email}
          onChange={(value) => this.handleEmailChange(value)}
          label="Email"
          type="email"
          helpText={
            <span>
              Enter your email Address.
            </span>
          }
        />

             <TextField
          value={email}
          onChange={(value) => this.handleEmailChange(value)}
          label="Password"
          type="password"
          helpText={
            <span>
              Enter your email Password.
            </span>
          }
        />
         <Checkbox
          label="Sign up for the Polaris newsletter"
          checked={newsletter}
          onChange={(value)=>this.handleNewsLetterChange(value)}
        />
        <Button submit>Sign In</Button>
           </FormLayout>
    </Form>
        </Page>

    );
    }
}
