import App from 'next/app';
import Head from 'next/head';
import { AppProvider,Frame,FooterHelp,Link,TopBar,Navigation} from '@shopify/polaris';
import {ArrowLeftMinor, ConversationMinor, HomeMajorMonotone, OrdersMajorTwotone} from '@shopify/polaris-icons';
import translations from '@shopify/polaris/locales/en.json';
import '@shopify/polaris/styles.css';
import { Provider } from '@shopify/app-bridge-react';
import Cookies from 'js-cookie';
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from 'react-apollo';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';
const client = new ApolloClient({
  fetchOptions: {
    credentials: 'include'
  },
});
class MyApp extends App {  
  state={
    isUserMenuOpen:false,
    toggleIsUserMenuOpen:false,
    mobileNavigationActive:false,
    userRole:store.get('userRole')
  };
  handleNavigationToggle(){
    this.setState({mobileNavigationActive:!this.state.mobileNavigationActive})
    console.log('toggle navigation visibility '+this.state.mobileNavigationActive);
  }
  toggleIsUserMenuOpen(){

    this.setState({isUserMenuOpen:!this.state.isUserMenuOpen})
    console.log(this.state.isUserMenuOpen)
  }
  render() {
    const { Component, pageProps } = this.props;
    const{isUserMenuOpen,mobileNavigationActive}=this.state;
    const userMenuMarkup = (
      <TopBar.UserMenu
        actions={[
          {
            items: [{content: 'Back to Shopify', icon: ArrowLeftMinor}],
          },
          {
            items: [{content: 'Community forums'}],
          },
        ]}
        name="Kuldeep"
        detail="Kuldeep Singh"
        initials="K"
        open={isUserMenuOpen}
        onToggle={()=>this.toggleIsUserMenuOpen()}
      />
    );
    let  menuitems=[];
    if(this.state.userRole=='dropshipper'){
      menuitems=[
        {
          label: 'DropShipper Dashboard',
          icon: HomeMajorMonotone,
          onClick: ()=>{},
        },
        {
          label: 'Search Products',
          icon: OrdersMajorTwotone,
          url:'/search'
        },
      ]
       }else{
        menuitems=[
          {
            label: 'Seller Dashboard',
            icon: HomeMajorMonotone,
            onClick: ()=>{},
          },
          {
            label: 'Jaded Pixel Orders',
            icon: OrdersMajorTwotone,
            onClick: ()=>{},
          },
        ]
    }
      const navigationMarkup = (
        <Navigation location="/">
          <Navigation.Section
            items={[
              {
                label: 'Back to Shopify',
                icon: ArrowLeftMinor,
              },
            ]}
          />
          <Navigation.Section
            separator
            title="Jaded Pixel App"

            items={menuitems}
            action={{
              icon: ConversationMinor,
              accessibilityLabel: 'Contact support',
              onClick: ()=>{},
            }}
          />
        </Navigation>
      );



    const topBarMarkup = (
      <TopBar
        showNavigationToggle
        userMenu={userMenuMarkup}
        onNavigationToggle={()=>this.handleNavigationToggle()}

      />
    );
    const config = { apiKey: API_KEY, shopOrigin: Cookies.get("shopOrigin"), forceRedirect: false };
    return (
      <React.Fragment>
        <Head>
          <title>Sample App</title>
          <meta charSet="utf-8" />
        </Head> 
        <Provider config={config}>     
        <AppProvider  i18n={translations}>
        <Frame topBar={topBarMarkup} 
        navigation={navigationMarkup}
        showMobileNavigation={mobileNavigationActive}
        onNavigationDismiss={()=>this.handleNavigationToggle()}
        >
        <ApolloProvider client={client}>
            <Component {...pageProps} />
            <FooterHelp>
  Learn more about{' '}
  <Link url="https://help.shopify.com/manual/orders/fulfill-orders">
    fulfilling orders
  </Link>
</FooterHelp>
          </ApolloProvider>
         </Frame>
          </AppProvider> 
          </Provider>       
      </React.Fragment>
    );
  }
}
export default MyApp;
