import { Layout, Page, TextStyle,EmptyState} from '@shopify/polaris';
import { TitleBar,ResourcePicker } from '@shopify/app-bridge-react';
import ResourceListWithProducts from '../components/ResourceList';
import store from 'store-js';
const axios =require('axios');
const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';
class Index extends React.Component {
  state = { open: false,userRole:store.get('userRole') };
   handleSelection = (resources) => {
    this.setState({ open: false })
     //console.log(resources)
     const idsFromResources = resources.selection.map((product) => product.id);
     const url="https://aladdin-dropshipper-server.herokuapp.com/products/new";
    store.set('ids', idsFromResources);
     var product={title:resources.selection[0].title,body_html:resources.selection[0].descriptionHtml,tags:'["dsfds","cloth"]',Variants:'dsfdsf',Collection:'fdsfd',product_type:resources.selection[0].productType,vendor:resources.selection[0].vendor};
     console.log(product)
     axios.post(url,product).then( ( response ) => {
      console.log(response.data )
      store.set('product',response.data);

      //  this.toggleSuccessActive();   

    } ).catch(function (error) {
      //console.log(error);
    })
  }

  render() {
    const { open,userRole }=this.state;
    const emptyState = !store.get('ids');
    if(userRole=='seller'){
    return (

  <Page>
    <TitleBar
      primaryAction={{
        content: 'Select products',
        onAction: () => this.setState({ open: true }),
      }}
    />
    <ResourcePicker
          resourceType="Product"
          showVariants={true}
          open={this.state.open}
          onSelection={(resources) => this.handleSelection(resources)}
          onCancel={() => this.setState({ open: false })}
        />
         {emptyState ? (
    <Layout>   
      <EmptyState
        heading="Sell Your Products on Multiple Stores"
        action={{
          content: 'Select products',
          onAction: () => this.setState({ open: true }),
        }}
        image={img}
      >
        <p>Select products to sale .</p>
      </EmptyState>
      </Layout>
       ) : (
        <ResourceListWithProducts />
        )}
    </Page>

);
}else{
  return (

    <Page>
 <EmptyState
        heading="Only Seller Can Sell Products"
        action={{
          content: 'Register As Seller',
          onAction: () =>{},
        }}
        image={img}
      ></EmptyState>
      </Page>
  )}
}
}

export default Index;
